INSERT INTO `ptcms_config` (`id`, `title`, `key`, `intro`, `type`, `group`, `extra`, `value`, `level`, `ordernum`, `create_user_id`, `update_user_id`, `create_time`, `update_time`, `status`) VALUES	(63, '页面标识', 'pagesign', '页面标识和控制器之间映射', 'array', 0, '', '{\"novelsearch.index.index\":\"index\",\"novelsearch.list.top\":\"top\",\"novelsearch.list.category\":\"category\",\"novelsearch.novel.index\":\"info\",\"novelsearch.novel.author\":\"author\",\"novelsearch.novel.dir\":\"dir\",\"novelsearch.chapter.list\":\"chapterlist\",\"novelsearch.chapter.frame\":\"frame\",\"novelsearch.chapter.go\":\"go\",\"novelsearch.chapter.green\":\"green\",\"novelsearch.chapter.read\":\"read\",\"novelsearch.search.result\":\"search\",\"page.index.detail\":\"page\",\"sitemap.index.index\":\"sitemapindex\",\"sitemap.index.info\":\"sitemapinfo\",\"novelsearch.list.over\":\"over\",\"novelsearch.novel.readend\":\"readend\",\"novelsearch.index.top\":\"topindex\",\"novelsearch.index.category\":\"categoryindex\",\"novelsearch.search.index\":\"searchindex\"}', 1, 50, 1, 1, 1414891596, 1454477411, 1);
INSERT INTO `ptcms_config` (`id`, `title`, `key`, `intro`, `type`, `group`, `extra`, `value`, `level`, `ordernum`, `create_user_id`, `update_user_id`, `create_time`, `update_time`, `status`) VALUES	(64, '链接生成规则', 'url_rules', '', 'array', 0, '', '{\"novelsearch.index.index\":\"\\/\",\"novelsearch.novel.index\":\"\\/novel\\/{novelkey}\\/\",\"novelsearch.chapter.list\":\"\\/novel\\/{novelkey}\\/[{sitekey}\\/]list[_{page}].html\",\"novelsearch.novel.dir\":\"\\/dir\\/{novelkey}\\/[{sitekey}\\/]\",\"novelsearch.novel.author\":\"\\/author\\/{author}\",\"novelsearch.chapter.read\":\"\\/novel\\/{novelkey}\\/read_{chapterid}.html\",\"novelsearch.chapter.frame\":\"\\/novel\\/{novelkey}\\/kj_{chapterid}.html\",\"novelsearch.chapter.go\":\"\\/novel\\/{novelkey}\\/go_{chapterid}.html\",\"novelsearch.novel.readend\":\"\\/novel\\/{novelkey}\\/readend.html\",\"novelsearch.list.over\":\"\\/over\\/[{page}.html]\",\"novelsearch.index.category\":\"\\/sort\\/\",\"novelsearch.index.top\":\"\\/top\\/\",\"novelsearch.search.index\":\"\\/search\\/\",\"novelsearch.chapter.green\":\"\\/novel\\/{novelkey}\\/tc_{chapterid}.html\",\"novelsearch.list.category\":\"\\/sort[\\/{key}][\\/{page}].html\",\"novelsearch.list.top\":\"\\/top[\\/{type}][\\/{page}].html\",\"page.index.detail\":\"\\/about\\/{key}.html\",\"novelsearch.search.result\":\"\\/search.html\",\"sitemap.index.index\":\"\\/sitemap\\/index.xml\",\"sitemap.index.info\":\"\\/sitemap\\/{page}.xml\"}', 1, 50, 1, 0, 1414892933, 0, 1);
INSERT INTO `ptcms_config` (`id`, `title`, `key`, `intro`, `type`, `group`, `extra`, `value`, `level`, `ordernum`, `create_user_id`, `update_user_id`, `create_time`, `update_time`, `status`) VALUES	(65, '路由映射规则', 'url_router', '', 'array', 0, '', '{\"^novel\\/([a-zA-Z0-9]+)(\\\\?(.*))*$\":\"novelsearch\\/novel\\/index?novelkey\",\"^novel\\/([a-zA-Z0-9]+)\\/([a-zA-Z0-9]+)\\/list_(\\\\d+).html(\\\\?(.*))*$\":\"novelsearch\\/chapter\\/list?novelkey&sitekey&page\",\"^novel\\/([a-zA-Z0-9]+)\\/list_(\\\\d+).html(\\\\?(.*))*$\":\"novelsearch\\/chapter\\/list?novelkey&page\",\"^novel\\/([a-zA-Z0-9]+)\\/([a-zA-Z0-9]+)\\/list.html(\\\\?(.*))*$\":\"novelsearch\\/chapter\\/list?novelkey&sitekey\",\"^novel\\/([a-zA-Z0-9]+)\\/list.html(\\\\?(.*))*$\":\"novelsearch\\/chapter\\/list?novelkey\",\"^dir\\/([a-zA-Z0-9]+)\\/([a-zA-Z0-9]+)(\\\\?(.*))*$\":\"novelsearch\\/novel\\/dir?novelkey&sitekey\",\"^dir\\/([a-zA-Z0-9]+)(\\\\?(.*))*$\":\"novelsearch\\/novel\\/dir?novelkey\",\"^author\\/([^\\\\..]+?)(\\\\?(.*))*$\":\"novelsearch\\/novel\\/author?author\",\"^novel\\/([a-zA-Z0-9]+)\\/read_(\\\\d+).html(\\\\?(.*))*$\":\"novelsearch\\/chapter\\/read?novelkey&chapterid\",\"^novel\\/([a-zA-Z0-9]+)\\/kj_(\\\\d+).html(\\\\?(.*))*$\":\"novelsearch\\/chapter\\/frame?novelkey&chapterid\",\"^novel\\/([a-zA-Z0-9]+)\\/go_(\\\\d+).html(\\\\?(.*))*$\":\"novelsearch\\/chapter\\/go?novelkey&chapterid\",\"^novel\\/([a-zA-Z0-9]+)\\/readend.html(\\\\?(.*))*$\":\"novelsearch\\/novel\\/readend?novelkey\",\"^over\\/(\\\\d+).html(\\\\?(.*))*$\":\"novelsearch\\/list\\/over?page\",\"^over(\\\\?(.*))*$\":\"novelsearch\\/list\\/over?\",\"^sort(\\\\?(.*))*$\":\"novelsearch\\/index\\/category?\",\"^top(\\\\?(.*))*$\":\"novelsearch\\/index\\/top?\",\"^search(\\\\?(.*))*$\":\"novelsearch\\/search\\/index?\",\"^novel\\/([a-zA-Z0-9]+)\\/tc_(\\\\d+).html(\\\\?(.*))*$\":\"novelsearch\\/chapter\\/green?novelkey&chapterid\",\"^sort\\/([a-zA-Z0-9]+)\\/(\\\\d+).html(\\\\?(.*))*$\":\"novelsearch\\/list\\/category?key&page\",\"^sort\\/(\\\\d+).html(\\\\?(.*))*$\":\"novelsearch\\/list\\/category?page\",\"^sort\\/([a-zA-Z0-9]+).html(\\\\?(.*))*$\":\"novelsearch\\/list\\/category?key\",\"^sort.html(\\\\?(.*))*$\":\"novelsearch\\/list\\/category?\",\"^top\\/([a-zA-Z0-9]+)\\/(\\\\d+).html(\\\\?(.*))*$\":\"novelsearch\\/list\\/top?type&page\",\"^top\\/(\\\\d+).html(\\\\?(.*))*$\":\"novelsearch\\/list\\/top?page\",\"^top\\/([a-zA-Z0-9]+).html(\\\\?(.*))*$\":\"novelsearch\\/list\\/top?type\",\"^top.html(\\\\?(.*))*$\":\"novelsearch\\/list\\/top?\",\"^about\\/([a-zA-Z0-9]+).html(\\\\?(.*))*$\":\"page\\/index\\/detail?key\",\"^search.html(\\\\?(.*))*$\":\"novelsearch\\/search\\/result?\",\"^sitemap\\/index.xml(\\\\?(.*))*$\":\"sitemap\\/index\\/index?\",\"^sitemap\\/(\\\\d+).xml(\\\\?(.*))*$\":\"sitemap\\/index\\/info?page\"}', 1, 50, 1, 0, 1414892966, 0, 1);
