-- 增加用户喜好表;
CREATE TABLE `ptcms_user_habbit` (
  `id` int(11) unsigned NOT NULL AUTO_INCREMENT,
  `userid` int(11) NOT NULL DEFAULT '0',
  `pc` text NOT NULL,
  `wap` text NOT NULL,
  `app` text NOT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `userid` (`userid`)
) ENGINE=myisam DEFAULT CHARSET=utf8;

